"use client";
import { useEffect, useRef } from "react";

type DefaultUpdate = { node?: string; status?: string };

type Handlers<TDone, TUpdate = DefaultUpdate> = {
  onUpdate?: (data: TUpdate) => void;
  onDone?: (data: TDone) => void;
  onError?: (data: { message?: string }) => void;
};

export function useSSE<TDone, TUpdate = DefaultUpdate>(
  url: string,
  body: Record<string, unknown>,
  { onUpdate, onDone, onError }: Handlers<TDone, TUpdate>
) {
  const abortRef = useRef<AbortController | null>(null);

  useEffect(() => {
    // Abort any in-flight request for prior effect
    try { abortRef.current?.abort(); } catch {}
    const ac = new AbortController();
    abortRef.current = ac;

    (async () => {
      const isAbortError = (err: any) =>
        err?.name === "AbortError" || err?.code === 20 || `${err?.message ?? ""}`.toLowerCase().includes("abort");

      try {
        const res = await fetch(url, {
          method: "POST",
          body: JSON.stringify(body),
          headers: { "Content-Type": "application/json" },
          signal: ac.signal,
        });
        if (ac.signal.aborted) return; // aborted early
        if (!res.ok) {
          onError?.({ message: `HTTP ${res.status} ${res.statusText}` });
          return;
        }
        if (!res.body) {
          onError?.({ message: "No response body" });
          return;
        }

        const reader = res.body.getReader();
        const decoder = new TextDecoder();
        let buf = "";
        let sawDone = false;
        try {
          while (!ac.signal.aborted) {
            const { done, value } = await reader.read();
            if (done || ac.signal.aborted) break;
            buf += decoder.decode(value, { stream: true });

            // SSE delimiter: blank line (\n\n or \r\n\r\n)
            const findDelimiter = () => {
              const nn = buf.indexOf("\n\n");
              const rr = buf.indexOf("\r\n\r\n");
              if (nn === -1) return rr;
              if (rr === -1) return nn;
              return Math.min(nn, rr);
            };
            let idx: number;
            while ((idx = findDelimiter()) !== -1) {
              const chunk = buf.slice(0, idx);
              // advance by the delimiter length (2 for \n\n, 4 for \r\n\r\n)
              const advance = buf.slice(idx, idx + 4) === "\r\n\r\n" ? 4 : 2;
              buf = buf.slice(idx + advance);

              // parse lines
              let event = "message";
              let data = "";
              for (const raw of chunk.split(/\r?\n/)) {
                const line = raw.trimEnd();
                if (line.startsWith("event:")) event = line.slice(6).trim();
                if (line.startsWith("data:")) data += line.slice(5).trim();
              }
              try {
                const json = (data ? JSON.parse(data) : undefined) as unknown;
                if (event === "update") onUpdate?.(json as TUpdate);
                else if (event === "done") { sawDone = true; onDone?.(json as TDone); }
                else if (event === "error") onError?.(json as { message?: string });
              } catch {
                // ignore JSON parse errors
              }
            }
          }
        } catch (err) {
          if (!isAbortError(err)) onError?.({ message: (err as any)?.message ?? "stream error" });
        } finally {
          try { reader.releaseLock(); } catch {}
          // flush any remaining decoded text and parse once more
          try {
            buf += decoder.decode();
            // best-effort: parse last chunk without requiring trailing delimiter
            const chunks = buf.split(/\n\n|\r\n\r\n/);
            for (const chunk of chunks) {
              if (!chunk.trim()) continue;
              let event = "message";
              let data = "";
              for (const raw of chunk.split(/\r?\n/)) {
                const line = raw.trimEnd();
                if (line.startsWith("event:")) event = line.slice(6).trim();
                if (line.startsWith("data:")) data += line.slice(5).trim();
              }
              try {
                const json = (data ? JSON.parse(data) : undefined) as unknown;
                if (event === "update") onUpdate?.(json as TUpdate);
                else if (event === "done") { sawDone = true; onDone?.(json as TDone); }
                else if (event === "error") onError?.(json as { message?: string });
              } catch {}
            }
          } catch {}
          if (!sawDone && !ac.signal.aborted) {
            onError?.({ message: "Stream ended without done" });
          }
        }
      } catch (err) {
        if (!isAbortError(err)) onError?.({ message: (err as any)?.message ?? "request error" });
      }
    })();

    return () => {
      try { ac.abort(); } catch {}
    };
  }, [url, JSON.stringify(body)]);
}

"use client";
import * as React from "react";
import {
  DndContext,
  closestCenter,
  KeyboardSensor,
  PointerSensor,
  useSensor,
  useSensors,
  DragEndEvent,
} from "@dnd-kit/core";
import {
  arrayMove,
  SortableContext,
  verticalListSortingStrategy,
  useSortable,
  sortableKeyboardCoordinates,
} from "@dnd-kit/sortable";
import { CSS } from "@dnd-kit/utilities";

type SortableListProps = {
  ids: string[];
  label: string;
  onReorder: (ids: string[]) => void;
  renderItem: (id: string, index: number) => React.ReactNode;
};

export function SortableList({ ids, label, onReorder, renderItem }: SortableListProps) {
  const [items, setItems] = React.useState(ids);
  React.useEffect(() => setItems(ids), [ids.join(",")]);

  const sensors = useSensors(
    useSensor(PointerSensor, { activationConstraint: { distance: 4 } }),
    useSensor(KeyboardSensor, { coordinateGetter: sortableKeyboardCoordinates })
  );

  function onDragEnd(event: DragEndEvent) {
    const { active, over } = event;
    if (!over || active.id === over.id) return;
    const oldIndex = items.indexOf(String(active.id));
    const newIndex = items.indexOf(String(over.id));
    const next = arrayMove(items, oldIndex, newIndex);
    setItems(next);
    onReorder(next);
  }

  const announcements = {
    onDragStart(id: string) {
      return `${label}: ${id} „ÇíÁßªÂãï‰∏≠`;
    },
    onDragOver(id: string, overId: string) {
      return `${label}: ${id} „Çí ${overId} „ÅÆ‰ΩçÁΩÆ„Å∏`;
    },
    onDragEnd(id: string, overId: string) {
      return `${label}: ${id} „Çí ${overId} „ÅÆ‰ΩçÁΩÆ„Å´ÁßªÂãï„Åó„Åæ„Åó„Åü`;
    },
    onDragCancel(id: string) {
      return `${label}: ${id} „ÅÆÁßªÂãï„Çí„Ç≠„É£„É≥„Çª„É´`;
    },
  } as const;

  return (
    <DndContext
      sensors={sensors}
      collisionDetection={closestCenter}
      onDragEnd={onDragEnd}
      accessibility={{
        announcements: {
          onDragStart({ active }) {
            return announcements.onDragStart(String(active.id));
          },
          onDragOver({ active, over }) {
            if (!over) return;
            return announcements.onDragOver(String(active.id), String(over.id));
          },
          onDragEnd({ active, over }) {
            if (!over) return;
            return announcements.onDragEnd(String(active.id), String(over.id));
          },
          onDragCancel({ active }) {
            return announcements.onDragCancel(String(active.id));
          },
        },
      }}
    >
      <SortableContext items={items} strategy={verticalListSortingStrategy}>
        <ul className="space-y-2">
          {items.map((id, index) => (
            <SortableRow key={id} id={id} index={index}>
              {renderItem(id, index)}
            </SortableRow>
          ))}
        </ul>
      </SortableContext>
    </DndContext>
  );
}

function SortableRow({ id, index, children }: { id: string; index: number; children: React.ReactNode }) {
  const { attributes, listeners, setNodeRef, transform, transition, isDragging } = useSortable({ id });
  const style: React.CSSProperties = {
    transform: CSS.Transform.toString(transform),
    transition,
    opacity: isDragging ? 0.8 : undefined,
  };
  return (
    <li ref={setNodeRef} style={style} className="rounded-md border border-[hsl(var(--border))] bg-[hsl(var(--card))] p-3">
      <div className="flex items-center gap-1 sm:gap-2">
        <button
          aria-label={`„Éâ„É©„ÉÉ„Ç∞„Åó„Å¶‰∏¶„Å≥Êõø„ÅàÔºà#${index + 1})`}
          className="cursor-grab select-none text-gray-500 px-1"
          {...attributes}
          {...listeners}
          type="button"
        >
          ‚â°
        </button>
        <span className="text-gray-500 select-none hidden sm:inline">#{index + 1}</span>
        <div className="flex-1 min-w-0">{children}</div>
      </div>
    </li>
  );
}

"use client";
import { useEffect } from "react";

export type HotkeyMap = Record<string, (e: KeyboardEvent) => void>;

function isEditableTarget(el: EventTarget | null) {
  const t = el as HTMLElement | null;
  if (!t) return false;
  const tag = t.tagName?.toLowerCase();
  return tag === "input" || tag === "textarea" || (t as any).isContentEditable === true;
}

// Simple hotkey hook; ignores events when typing in inputs/textarea/contentEditable
export function useHotkeys(map: HotkeyMap, deps: unknown[] = []) {
  useEffect(() => {
    function onKey(e: KeyboardEvent) {
      if (isEditableTarget(e.target)) return;
      const key = normalizeKey(e);
      const fn = map[key] || map[e.key];
      if (fn) {
        fn(e);
      }
    }
    window.addEventListener("keydown", onKey);
    return () => window.removeEventListener("keydown", onKey);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, deps);
}

function normalizeKey(e: KeyboardEvent) {
  const parts: string[] = [];
  if (e.ctrlKey) parts.push("Ctrl");
  if (e.metaKey) parts.push("Meta");
  if (e.shiftKey) parts.push("Shift");
  if (e.altKey) parts.push("Alt");
  parts.push(e.key);
  return parts.join("+");
}

