"use client";
import * as React from "react";
import { Card } from "@/components/ui/card";

export type SseLog = { ts: number; text: string };

type Step = {
  label: string;
  start: number;
  end?: number;
};

function toSteps(logs: SseLog[]): Step[] {
  const steps: Step[] = [];
  for (const l of logs) {
    const label = (l.text || "").trim();
    if (!label) continue;
    const prev = steps[steps.length - 1];
    if (prev && prev.label === label && !prev.end) {
      prev.end = l.ts; // refresh end for consecutive duplicates
    } else {
      steps.push({ label, start: l.ts });
    }
  }
  return steps;
}

function fmt(ms: number) {
  if (ms < 1000) return `${ms}ms`;
  const s = Math.round(ms / 100) / 10;
  return `${s}s`;
}

export function SSETimeline({ logs }: { logs: SseLog[] }) {
  const steps = toSteps(logs);
  const now = Date.now();
  const lastError = [...steps].reverse().find((s) => /^„Ç®„É©„Éº/.test(s.label) || /error/i.test(s.label));

  type GroupId = "prep" | "gen" | "validate" | "persist" | "other";
  type Group = { id: GroupId; label: string; icon: string; start?: number; end?: number };
  const GROUPS: Group[] = [
    { id: "prep", label: "Ê∫ñÂÇô", icon: "üß∞" },
    { id: "gen", label: "ÁîüÊàê", icon: "‚ú®" },
    { id: "validate", label: "Ê§úË®º", icon: "üß™" },
    { id: "persist", label: "‰øùÂ≠ò", icon: "üíæ" },
  ];

  const matchGroup = (label: string): GroupId => {
    if (/(received|normalizeInput|expandContext)/i.test(label)) return "prep";
    if (/(planCourse|generateCards)/i.test(label)) return "gen";
    if (/(validatePlan|validateSchema)/i.test(label)) return "validate";
    if (/(persistPreview|‰øùÂ≠ò)/i.test(label)) return "persist";
    return "other";
  };

  // Determine first appearance times for each group in logical order
  const groupIndex = new Map<GroupId, number>();
  steps.forEach((s, idx) => {
    const gid = matchGroup(s.label);
    if (!groupIndex.has(gid)) groupIndex.set(gid, idx);
  });
  const groupsWithTimes = GROUPS.map((g, i) => {
    const idx = groupIndex.get(g.id);
    const start = idx != null ? steps[idx].start : undefined;
    // end is the start of the next seen group, else last seen step of this group, else now
    let end: number | undefined = undefined;
    if (start != null) {
      // find first later group that has a start
      for (let j = i + 1; j < GROUPS.length; j++) {
        const nextIdx = groupIndex.get(GROUPS[j].id);
        if (nextIdx != null) { end = steps[nextIdx].start; break; }
      }
      if (end == null) {
        // try to find last step of same group
        const lastOfGroup = [...steps].reverse().find((s) => matchGroup(s.label) === g.id);
        end = lastOfGroup?.end ?? now;
      }
    }
    return { ...g, start, end } as Group;
  });

  const anyStarted = groupsWithTimes.some((g) => g.start != null) || steps.length > 0;

  return (
    <Card className="p-3 text-xs text-gray-800 h-48 sm:h-64 overflow-auto" role="status" aria-live="polite">
      {lastError && <div role="alert" className="sr-only">{lastError.label}</div>}
      {lastError && (
        <div className="sticky top-0 z-10 mb-2 rounded border border-[hsl(var(--destructive))]/40 bg-[hsla(0,84%,60%,.08)] px-2 py-1 text-[11px] text-[hsl(var(--destructive))]">
          ‚ö†Ô∏é „Ç®„É©„Éº„ÅåÁô∫Áîü„Åó„Åæ„Åó„Åü: <span className="font-mono">{lastError.label}</span>
        </div>
      )}
      {!anyStarted ? (
        <p className="text-gray-500">ÂæÖÊ©ü‰∏≠‚Ä¶</p>
      ) : (
        <ol className="space-y-2">
          {groupsWithTimes.map((g) => {
            const started = g.start != null;
            const done = started && g.end != null && g.end < now && groupIndex.has(g.id) && GROUPS.indexOf(g as any) < GROUPS.length - 1 && groupsWithTimes.find((x) => x.id === g.id)?.end !== g.start; // heuristic
            const active = started && !done;
            const duration = started ? (g.end ?? now) - (g.start as number) : 0;
            const dotColor = lastError && active ? "hsl(var(--destructive))" : active ? "hsl(var(--primary))" : started ? "rgba(0,0,0,.55)" : "rgba(0,0,0,.25)";
            return (
              <li key={g.id} className="pl-5">
                <span
                  className="absolute -ml-4 mt-[.3rem] inline-block h-2 w-2 rounded-full"
                  style={{ backgroundColor: dotColor }}
                  aria-hidden
                />
                <div className="flex items-center justify-between gap-2">
                  <span className="font-mono">
                    {lastError && active ? "‚ö†Ô∏è " : active ? "‚è≥ " : started ? "‚úîÔ∏é " : "‚Ä¢ "}
                    {g.icon} {g.label}
                  </span>
                  {started && <span className="text-[10px] text-gray-500">{fmt(duration)}</span>}
                </div>
              </li>
            );
          })}
        </ol>
      )}
    </Card>
  );
}

"use client";
import * as React from "react";
import { useEffect, useMemo, useState } from "react";
import { useRouter } from "next/navigation";
import { Dialog, DialogContent, DialogTitle } from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { listCourses, listLessons, useLocalDbVersion } from "@/lib/localdb";

type Cmd = { id: string; label: string; hint?: string; action: () => void };

export function CommandPalette() {
  const router = useRouter();
  const [open, setOpen] = useState(false);
  const [q, setQ] = useState("");
  const [courses, setCourses] = useState(() => listCourses());
  const [lessons, setLessons] = useState<{ courseId: string; lessonId: string; title: string }[]>([]);

  const dbv = useLocalDbVersion();
  useEffect(() => {
    const cs = listCourses();
    setCourses(cs);
    const all = cs.flatMap((c) => listLessons(c.id).map((l) => ({ courseId: c.id, lessonId: l.id, title: `${c.title} / ${l.title}` })));
    setLessons(all);
  }, [dbv]);

  // Global shortcuts: Cmd/Ctrl+K toggles
  useEffect(() => {
    function onKey(e: KeyboardEvent) {
      if ((e.metaKey || e.ctrlKey) && e.key.toLowerCase() === "k") {
        e.preventDefault();
        setOpen((s) => !s);
      }
      if (e.key === "Escape") setOpen(false);
    }
    window.addEventListener("keydown", onKey);
    return () => window.removeEventListener("keydown", onKey);
  }, []);

  // External open via CustomEvent
  useEffect(() => {
    function onOpen() { setOpen(true); }
    window.addEventListener("open-command-palette", onOpen as any);
    return () => window.removeEventListener("open-command-palette", onOpen as any);
  }, []);

  const base: Cmd[] = [
    { id: "home", label: "„ÉÄ„ÉÉ„Ç∑„É•„Éú„Éº„Éâ„Å∏ÁßªÂãï", hint: "/", action: () => router.push("/") },
    { id: "plan", label: "AI„Åß„Ç≥„Éº„Çπ‰ΩúÊàê", hint: "/courses/plan", action: () => router.push("/courses/plan") },
    { id: "new", label: "ÊâãÂãï„Åß„Ç≥„Éº„Çπ‰ΩúÊàê", hint: "/courses/new", action: () => router.push("/courses/new") },
  ];

  const courseCmds: Cmd[] = courses.flatMap((c) => [
    { id: `open-${c.id}` , label: `„ÉØ„Éº„ÇØ„Çπ„Éö„Éº„Çπ„ÇíÈñã„Åè: ${c.title}`, hint: "/courses/[id]/workspace", action: () => router.push(`/courses/${c.id}/workspace`) },
  ]);

  const lessonCmds: Cmd[] = lessons.map((l) => ({
    id: `cards-${l.lessonId}`,
    label: `„ÉØ„Éº„ÇØ„Çπ„Éö„Éº„Çπ„ÅßÈñã„Åè: ${l.title}`,
    hint: "/courses/[id]/workspace",
    action: () => router.push(`/courses/${l.courseId}/workspace`),
  }));

  const cmds = useMemo(() => {
    const all = [...base, ...courseCmds, ...lessonCmds];
    const kw = q.trim().toLowerCase();
    if (!kw) return all;
    return all.filter((c) => c.label.toLowerCase().includes(kw));
  }, [q, courses, lessons]);

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogContent className="p-0 overflow-hidden w-[calc(100vw-1.5rem)] sm:w-auto sm:max-w-xl">
        <DialogTitle className="sr-only">„Ç≥„Éû„É≥„Éâ„Éë„É¨„ÉÉ„Éà</DialogTitle>
        <div className="border-b border-[hsl(var(--border))] p-3">
          <label className="sr-only" htmlFor="cmdk-input">„Ç≥„Éû„É≥„ÉâÊ§úÁ¥¢</label>
          <Input
            id="cmdk-input"
            placeholder="Ê§úÁ¥¢„Åæ„Åü„ÅØ„Ç≥„Éû„É≥„Éâ‚Ä¶ (‚åòK)"
            autoFocus
            value={q}
            onChange={(e) => setQ(e.target.value)}
          />
        </div>
        <div className="max-h-[60vh] overflow-auto">
          {cmds.length === 0 ? (
            <div className="p-4 text-sm text-gray-600">Ë©≤ÂΩì„Åô„Çã„Ç≥„Éû„É≥„Éâ„ÅØ„ÅÇ„Çä„Åæ„Åõ„Çì„ÄÇ</div>
          ) : (
            <ul role="listbox" aria-label="„Ç≥„Éû„É≥„ÉâÁµêÊûú" className="divide-y divide-[hsl(var(--border))]">
              {cmds.map((c) => (
                <li key={c.id} role="option">
                  <button
