  const [saving, setSaving] = React.useState<"idle" | "saving" | "saved">("idle");
  const [savedAt, setSavedAt] = React.useState<string | null>(null);
  const debounceRef = React.useRef<number | null>(null);
  const [lessons, setLessons] = React.useState<Lesson[]>([]);
  const [cards, setCards] = React.useState<Card[]>([]);
  const [newLessonTitle, setNewLessonTitle] = React.useState("");
  const [newCard, setNewCard] = React.useState<{ type: CardType; title: string; body?: string; question?: string; options?: string; answerIndex?: number; explanation?: string; text?: string; answers?: string; caseSensitive?: boolean }>({ type: "text", title: "", body: "" });

  // AI lesson-cards generation state
  const [runningLesson, setRunningLesson] = React.useState<Lesson | null>(null);
  const [logsByLesson, setLogsByLesson] = React.useState<Record<string, { ts: number; text: string }[]>>({});
  const [previews, setPreviews] = React.useState<Record<string, { draftId: string; payload: LessonCards }>>({});
  const [selectedIndexes, setSelectedIndexes] = React.useState<Record<string, Record<number, boolean>>>({});

  function refreshLists() {
    setCourse(getCourse(courseId) ?? null);
    const ls = listLessons(courseId);
    setLessons(ls);
    if (selectedKind === "lesson" && selectedId) {
      const l = ls.find((x) => x.id === selectedId) ?? null;
      setLesson(l);
      setCards(l ? listCards(l.id) : []);
    } else if (selectedKind === "card" && selectedId) {
      const l = ls.find((x) => listCards(x.id).some((c) => c.id === selectedId));
      setLesson(null);
      setCards([]);
      const found = (l ? listCards(l.id) : []).find((c) => c.id === selectedId) ?? null;
      setCard(found ?? null);
    } else {
      setLesson(null); setCards([]); setCard(null);
    }
  }

  React.useEffect(() => {
    refreshLists();
  }, [courseId, selectedId, selectedKind, dbv]);

  // ‰∏ãÊõ∏„Åç or ÁèæË°åÂÄ§„Åß„Éï„Ç©„Éº„É†ÂàùÊúüÂåñ
  React.useEffect(() => {
    let mounted = true;
    (async () => {
      if (!card) { setForm(null); return; }
      const draft = await loadCardDraft(card.id);
      if (!mounted) return;
      if (draft) { setForm(draft); return; }
      if (card.cardType === "text") {
        setForm({ cardId: card.id, cardType: "text", title: card.title ?? null, tags: card.tags ?? [], body: (card.content as any).body ?? "" });
      } else if (card.cardType === "quiz") {
        const c = card.content as QuizCardContent;
        setForm({ cardId: card.id, cardType: "quiz", title: card.title ?? null, tags: card.tags ?? [], question: c.question, options: c.options, answerIndex: c.answerIndex, explanation: c.explanation ?? null });
      } else {
        const c = card.content as FillBlankCardContent;
        setForm({ cardId: card.id, cardType: "fill-blank", title: card.title ?? null, tags: card.tags ?? [], text: c.text, answers: c.answers, caseSensitive: !!c.caseSensitive });
      }
    })();
    return () => { mounted = false; };
  }, [card?.id]);

  // 500ms „Éá„Éê„Ç¶„É≥„Çπ„ÅÆ„Ç™„Éº„Éà„Çª„Éº„Éñ
  React.useEffect(() => {
    if (!form) return;
    setSaving("saving");
    if (debounceRef.current) window.clearTimeout(debounceRef.current);
    debounceRef.current = window.setTimeout(async () => {
      const res = await saveCardDraft(form);
      setSavedAt(res.updatedAt);
      setSaving("saved");
    }, 500);
    return () => {
      if (debounceRef.current) window.clearTimeout(debounceRef.current);
    };
  }, [JSON.stringify(form)]);

  // ÁèæÂú®„ÅÆ„É¨„ÉÉ„Çπ„É≥Ôºà„Ç´„Éº„ÉâÈÅ∏ÊäûÊôÇ„ÇÇË¶™„É¨„ÉÉ„Çπ„É≥„ÇíËß£Ê±∫Ôºâ
  const currentLesson: Lesson | null = React.useMemo(() => {
    if (selectedKind === "lesson" && selectedId) {
      return (listLessons(courseId).find((x) => x.id === selectedId) ?? null);
    }
    if (selectedKind === "card" && selectedId) {
      const ls = listLessons(courseId);
      const l = ls.find((x) => listCards(x.id).some((c) => c.id === selectedId)) ?? null;
      return l ?? null;
    }
    return null;
  }, [courseId, selectedKind, selectedId, dbv]);

  // „É¨„ÉÉ„Çπ„É≥‰∏äÈÉ®„ÉÑ„Éº„É´„ÅØ„Éï„Ç°„Ç§„É´„Çπ„Ç≥„Éº„Éó„ÅÆÂÆâÂÆö„Ç≥„É≥„Éù„Éº„Éç„É≥„Éà„Å´ÁßªÂãïÔºà‰∏ãÊñπ„ÅßÂÆöÁæ©Ôºâ

  return (
    <aside className="h-full overflow-auto p-3">
      <div className="text-xs text-gray-500 mb-2">„Ç§„É≥„Çπ„Éö„ÇØ„Çø</div>
      {currentLesson && (
        <LessonTools
          lesson={currentLesson}
          runningLesson={runningLesson}
          setRunningLesson={setRunningLesson}
          logsByLesson={logsByLesson}
          setLogsByLesson={setLogsByLesson}
          previews={previews}
          setPreviews={setPreviews}
          selectedIndexes={selectedIndexes}
          setSelectedIndexes={setSelectedIndexes}
          onSaveAll={async (lessonId, payload, selected) => {
            const idxs = Object.entries(selected).filter(([, v]) => v).map(([k]) => Number(k));
            const res = idxs.length > 0
              ? await commitLessonCardsPartial({ draftId: payload.draftId, lessonId, selectedIndexes: idxs })
              : await commitLessonCards({ draftId: payload.draftId, lessonId });
            if (!res) return alert("‰øùÂ≠ò„Å´Â§±Êïó„Åó„Åæ„Åó„Åü");
            refreshLists();
            setPreviews((prev) => { const copy = { ...prev }; delete copy[lessonId]; return copy; });
          }}
        />
      )}
      {!selectedId && (
        <p className="text-sm text-gray-700">„Ç≥„Éº„Çπ„ÇÑ„É¨„ÉÉ„Çπ„É≥/„Ç´„Éº„Éâ„ÇíÈÅ∏Êäû„Åó„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ</p>
      )}

      {/* Course-level: „É¨„ÉÉ„Çπ„É≥ÁÆ°ÁêÜ */}
      {!selectedId && course && (
        <CourseInspector course={course} lessons={lessons} onRefresh={refreshLists} />
      )}

      {/* Lesson-level: „Ç´„Éº„ÉâÁÆ°ÁêÜ + AIÁîüÊàê */}
      {selectedKind === "lesson" && lesson && (
        <LessonInspector
          courseId={courseId}
          lesson={lesson}
          cards={cards}
          runningLesson={runningLesson}
          setRunningLesson={setRunningLesson}
          logsByLesson={logsByLesson}
          setLogsByLesson={setLogsByLesson}
          previews={previews}
          setPreviews={setPreviews}
          selectedIndexes={selectedIndexes}
          setSelectedIndexes={setSelectedIndexes}
          hideAiSection
          onRefresh={refreshLists}
        />
      )}
      {selectedKind === "card" && card && form && (
        <section className="space-y-2">
          <h3 className="font-medium">„Ç´„Éº„ÉâÁ∑®ÈõÜ</h3>
          <div className="text-xs text-gray-500" aria-live="polite">
            „Çø„Ç§„Éó: {form.cardType} / ‰øùÂ≠ò: {saving === "saving" ? "‰øùÂ≠ò‰∏≠‚Ä¶" : saving === "saved" ? (savedAt ? `‰øùÂ≠òÊ∏à„ÅøÔºà${new Date(savedAt).toLocaleTimeString()}Ôºâ` : "‰øùÂ≠òÊ∏à„Åø") : "-"}
          </div>
          <div>
            <label className="block text-sm mb-1">„Çø„Ç§„Éà„É´Ôºà‰ªªÊÑèÔºâ</label>
            <Input value={(form as any).title ?? ""} onChange={(e) => setForm((f) => f ? ({ ...f, title: e.target.value }) as any : f)} />
          </div>
          <div>
            <label className="block text-sm mb-1">„Çø„Ç∞Ôºà„Ç´„É≥„ÉûÂå∫Âàá„ÇäÔºâ</label>
            <Input
              value={(form as any).tags?.join(", ") ?? ""}
              onChange={(e) => setForm((f) => f ? ({ ...f, tags: e.target.value.split(",").map((s)=>s.trim()).filter(Boolean) }) as any : f)}
              placeholder="‰æã: Âü∫Á§é, ÈáçË¶Å, Áî®Ë™û"
            />
          </div>
          {form.cardType === "text" && (
            <div>
              <label className="block text-sm mb-1">Êú¨Êñá</label>
              <Textarea value={(form as any).body ?? ""} onChange={(e) => setForm((f) => f ? ({ ...f, body: e.target.value }) as any : f)} />
            </div>
          )}
          {form.cardType === "quiz" && (
            <div className="grid grid-cols-1 gap-2">
              <div>
                <label className="block text-sm mb-1">Ë®≠Âïè</label>
                <Input value={(form as any).question ?? ""} onChange={(e) => setForm((f) => f ? ({ ...f, question: e.target.value }) as any : f)} />
              </div>
              <div>
                <label className="block text-sm mb-1">ÈÅ∏ÊäûËÇ¢ÔºàÊîπË°åÂå∫Âàá„ÇäÔºâ</label>
                <Textarea value={(form as any).options?.join("\n") ?? ""} onChange={(e) => setForm((f) => f ? ({ ...f, options: e.target.value.split("\n").map((s)=>s.trim()).filter(Boolean) }) as any : f)} />
              </div>
              <div>
                <label className="block text-sm mb-1">Ê≠£Ëß£„Ç§„É≥„Éá„ÉÉ„ÇØ„ÇπÔºà0ÈñãÂßãÔºâ</label>
                <Input type="number" value={(form as any).answerIndex ?? 0} onChange={(e) => setForm((f) => f ? ({ ...f, answerIndex: Number(e.target.value) }) as any : f)} />
              </div>
              <div>
                <label className="block text-sm mb-1">Ëß£Ë™¨Ôºà‰ªªÊÑèÔºâ</label>
                <Input value={(form as any).explanation ?? ""} onChange={(e) => setForm((f) => f ? ({ ...f, explanation: e.target.value }) as any : f)} />
              </div>
            </div>
          )}
          {form.cardType === "fill-blank" && (
            <div className="grid grid-cols-1 gap-2">
              <div>
                <label className="block text-sm mb-1">„ÉÜ„Ç≠„Çπ„ÉàÔºà[[1]] ÂΩ¢ÂºèÔºâ</label>
                <Textarea value={(form as any).text ?? ""} onChange={(e) => setForm((f) => f ? ({ ...f, text: e.target.value }) as any : f)} />
              </div>
              <div>
                <label className="block text-sm mb-1">ÂõûÁ≠îÔºàk:value ÊîπË°åÂå∫Âàá„ÇäÔºâ</label>
                <Textarea value={Object.entries((form as any).answers ?? {}).map(([k,v]) => `${k}:${v}`).join("\n")} onChange={(e) => {
                  const obj: Record<string,string> = {};
                  e.target.value.split("\n").map((s)=>s.trim()).filter(Boolean).forEach((line)=>{
                    const [k,...rest] = line.split(":");
                    const v = rest.join(":").trim();
                    if (k && v) obj[k.trim()] = v;
                  });
                  setForm((f)=> f ? ({ ...f, answers: obj }) as any : f);
                }} />
              </div>
            </div>
          )}
          <div className="flex justify-end gap-2">
            <Button variant="outline" onClick={() => setForm((f) => f ? ({ ...f }) : f)} disabled={saving === "saving"}>‰∏ãÊõ∏„Åç‰øùÂ≠òÊ∏à„Åø</Button>
            <Button onClick={async () => { if (card) { await publishCard(card.id); setSaving("idle"); } }}>ÂÖ¨ÈñãÔºàÂèçÊò†Ôºâ</Button>
          </div>
        </section>
      )}
      {course && (
        <section className="mt-4">
          <h4 className="font-medium text-sm">„Ç≥„Éº„Çπ</h4>
          <div className="text-sm">{course.title}</div>
        </section>
      )}
    </aside>
  );
}

