          <ol className="text-sm space-y-1 list-decimal list-inside">
            {previews[lesson.id].payload.cards.map((c, idx) => (
              <li key={idx} className="flex items-center gap-2">
                <input
                  type="checkbox"
                  aria-label={`„Ç´„Éº„Éâ #${idx + 1} „ÇíÈÅ∏Êäû`}
                  checked={!!(selectedIndexes[lesson.id]?.[idx])}
                  onChange={(e) => setSelectedIndexes((m) => ({ ...m, [lesson.id]: { ...(m[lesson.id] || {}), [idx]: e.target.checked } }))}
                />
                <span className="px-1 py-0.5 rounded bg-black/5 mr-2">{c.type}</span>
                {"title" in c && c.title ? c.title : c.type === "text" ? "„ÉÜ„Ç≠„Çπ„Éà" : "„Ç´„Éº„Éâ"}
              </li>
            ))}
          </ol>
          <div className="mt-3 flex justify-end gap-2">
            <Button onClick={() => onSaveAll(lesson.id, previews[lesson.id], selectedIndexes[lesson.id] || {})}>‰øùÂ≠ò</Button>
            <Button onClick={() => setPreviews((prev) => { const copy = { ...prev }; delete copy[lesson.id]; return copy; })}>Á†¥Ê£Ñ</Button>
          </div>
        </div>
      )}
    </section>
  );
}

function summary(c: Card): string {
  if (c.cardType === "text") return (c.content as any).body?.slice(0, 30) ?? "„ÉÜ„Ç≠„Çπ„Éà";
  if (c.cardType === "quiz") return (c.content as any).question ?? "„ÇØ„Ç§„Ç∫";
  return (c.content as any).text?.replace(/\n/g, " ").slice(0, 30) ?? "Á©¥Âüã„ÇÅ";
}

