                    onChange={(e) => setSelectedIndexes((m) => ({ ...m, [lesson.id]: { ...(m[lesson.id] || {}), [idx]: e.target.checked } }))}
                  />
                  <span className="px-1 py-0.5 rounded bg-black/5 mr-2">{c.type}</span>
                  {"title" in c && c.title ? c.title : c.type === "text" ? "„ÉÜ„Ç≠„Çπ„Éà" : "„Ç´„Éº„Éâ"}
                </li>
              ))}
            </ol>
            <div className="mt-3 flex justify-end gap-2">
              <Button
                onClick={() => {
                  const p = previews[lesson.id];
                  if (!p) return;
                  const selected = selectedIndexes[lesson.id] || {};
                  const idxs = Object.entries(selected).filter(([, v]) => v).map(([k]) => Number(k));
                  let ok = false;
                const res = idxs.length > 0
                  ? await commitLessonCardsPartial({ draftId: p.draftId, lessonId: lesson.id, selectedIndexes: idxs })
                  : await commitLessonCards({ draftId: p.draftId, lessonId: lesson.id });
                if (res) { onRefresh(); }
                  setPreviews((prev) => { const copy = { ...prev }; delete copy[lesson.id]; return copy; });
                }}
              >‰øùÂ≠ò</Button>
              <Button onClick={() => setPreviews((prev) => { const copy = { ...prev }; delete copy[lesson.id]; return copy; })}>Á†¥Ê£Ñ</Button>
            </div>
          </div>
        )}
      </div>
      )}

      {/* ÊâãÂãï„Åß„Ç´„Éº„ÉâËøΩÂä† */}
      <div className="rounded-md border border-[hsl(var(--border))] p-3">
        <div className="text-sm font-medium mb-2">Êñ∞Ë¶è„Ç´„Éº„Éâ</div>
        <NewCardForm lessonId={lesson.id} onDone={onRefresh} />
      </div>

      {/* „Ç´„Éº„Éâ‰∏ÄË¶ßÔºà‰∏¶„Å≥Êõø„Åà/ÂâäÈô§Ôºâ */}
      <div>
        <h4 className="text-sm text-gray-700 mb-2">„Ç´„Éº„Éâ‰∏ÄË¶ß</h4>
        {cards.length === 0 ? (
          <p className="text-sm text-gray-600">„Ç´„Éº„Éâ„Åå„ÅÇ„Çä„Åæ„Åõ„Çì„ÄÇ</p>
        ) : (
          <SortableList
            ids={cards.map((c) => c.id)}
            label="„Ç´„Éº„Éâ„ÅÆ‰∏¶„Å≥Êõø„Åà"
            onReorder={async (ids) => { await reorderCards(lesson.id, ids); onRefresh(); }}
            renderItem={(id) => {
              const c = cards.find((x) => x.id === id);
              if (!c) return <div className="text-xs text-gray-400">Êõ¥Êñ∞‰∏≠‚Ä¶</div>;
              return (
                <div className="flex items-center gap-2">
                  <span className="px-1 py-0.5 rounded bg-black/5 text-xs">{c.cardType}</span>
                  <span className="flex-1 truncate">{c.title || labelCard(c)}</span>
                  <Confirm
                    title="„Åì„ÅÆ„Ç´„Éº„Éâ„ÇíÂâäÈô§„Åó„Åæ„Åô„ÅãÔºü"
                    description="„Åì„ÅÆÊìç‰Ωú„ÅØÂÖÉ„Å´Êàª„Åõ„Åæ„Åõ„Çì„ÄÇÂ≠¶ÁøíÂ±•Ê≠¥„ÇÇÂâäÈô§„Åï„Çå„Åæ„Åô„ÄÇ"
                    confirmLabel="ÂâäÈô§„Åô„Çã"
                    cancelLabel="„Ç≠„É£„É≥„Çª„É´"
                    onConfirm={async () => { await deleteCard(c.id); onRefresh(); }}
                  >
                    <Button variant="destructive" size="sm">ÂâäÈô§</Button>
                  </Confirm>
                </div>
              );
            }}
          />
        )}
      </div>
    </section>
  );
}

function labelCard(card: Card): string {
  if (card.cardType === "text") return (card.content as any).body?.slice(0, 18) ?? "„ÉÜ„Ç≠„Çπ„Éà";
  if (card.cardType === "quiz") return (card.content as any).question ?? "„ÇØ„Ç§„Ç∫";
  return (card.content as any).text?.replace(/\[\[(\d+)\]\]/g, "‚ñ°") ?? "Á©¥Âüã„ÇÅ";
}

function NewCardForm({ lessonId, onDone }: { lessonId: UUID; onDone: () => void }) {
  const [type, setType] = React.useState<CardType>("text");
  const [title, setTitle] = React.useState("");
  const [body, setBody] = React.useState("");
  const [question, setQuestion] = React.useState("");
  const [options, setOptions] = React.useState("");
  const [answerIndex, setAnswerIndex] = React.useState(0);
  const [explanation, setExplanation] = React.useState("");
  const [text, setText] = React.useState("");
  const [answers, setAnswers] = React.useState("1:answer");
  const [caseSensitive, setCaseSensitive] = React.useState(false);

  return (
    <form
      onSubmit={async (e) => {
        e.preventDefault();
        if (type === "text") {
          if (!body.trim()) return alert("Êú¨Êñá„ÅØÂøÖÈ†à„Åß„Åô");
          await addCard(lessonId, { cardType: "text", title: title || null, content: { body } });
        } else if (type === "quiz") {
          const opts = options.split("\n").map((s) => s.trim()).filter(Boolean);
          if (!question.trim() || opts.length < 2) return alert("Ë®≠Âïè„Å®ÈÅ∏ÊäûËÇ¢2„Å§‰ª•‰∏ä„ÅåÂøÖË¶Å„Åß„Åô");
          await addCard(lessonId, { cardType: "quiz", title: title || null, content: { question, options: opts, answerIndex: Math.max(0, Math.min(answerIndex, opts.length - 1)), explanation: explanation || undefined } });
        } else {
          const obj: Record<string, string> = {};
          answers.split("\n").map((s) => s.trim()).filter(Boolean).forEach((line) => {
            const [k, ...rest] = line.split(":");
            const v = rest.join(":").trim();
            if (k && v) obj[k.trim()] = v;
          });
          if (!text.trim() || Object.keys(obj).length === 0) return alert("„ÉÜ„Ç≠„Çπ„Éà„Å®ÂõûÁ≠îÔºà‰æã: 1:answerÔºâ„ÇíÂÖ•Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑ");
          await addCard(lessonId, { cardType: "fill-blank", title: title || null, content: { text, answers: obj, caseSensitive } });
        }
        setTitle(""); setBody(""); setQuestion(""); setOptions(""); setAnswerIndex(0); setExplanation(""); setText(""); setAnswers("1:answer"); setCaseSensitive(false);
        onDone();
      }}
      className="space-y-3"
    >
      <div className="flex gap-3 items-center">
        <label className="text-sm">„Çø„Ç§„Éó</label>
        <Select value={type} onChange={(e) => setType(e.target.value as CardType)} className="max-w-[160px]">
          <option value="text">Text</option>
          <option value="quiz">Quiz</option>
          <option value="fill-blank">Fill‚Äëblank</option>
        </Select>
        <Input value={title} onChange={(e) => setTitle(e.target.value)} placeholder="„Çø„Ç§„Éà„É´Ôºà‰ªªÊÑèÔºâ" className="flex-1" />
      </div>
      {type === "text" && (
        <div>
          <label className="block text-sm mb-1">Êú¨Êñá</label>
          <Textarea value={body} onChange={(e) => setBody(e.target.value)} />
        </div>
      )}
      {type === "quiz" && (
        <div className="grid grid-cols-1 gap-2">
          <div>
            <label className="block text-sm mb-1">Ë®≠Âïè</label>
            <Input value={question} onChange={(e) => setQuestion(e.target.value)} />
          </div>
          <div>
            <label className="block text-sm mb-1">ÈÅ∏ÊäûËÇ¢ÔºàÊîπË°åÂå∫Âàá„ÇäÔºâ</label>
            <Textarea value={options} onChange={(e) => setOptions(e.target.value)} />
          </div>
          <div>
            <label className="block text-sm mb-1">Ê≠£Ëß£„Ç§„É≥„Éá„ÉÉ„ÇØ„ÇπÔºà0ÈñãÂßãÔºâ</label>
            <Input type="number" value={answerIndex} onChange={(e) => setAnswerIndex(Number(e.target.value))} />
          </div>
          <div>
            <label className="block text-sm mb-1">Ëß£Ë™¨Ôºà‰ªªÊÑèÔºâ</label>
            <Input value={explanation} onChange={(e) => setExplanation(e.target.value)} />
          </div>
        </div>
      )}
      {type === "fill-blank" && (
        <div className="grid grid-cols-1 gap-2">
          <div>
            <label className="block text-sm mb-1">„ÉÜ„Ç≠„Çπ„ÉàÔºà[[1]] „ÅÆÂΩ¢Âºè„ÅßÁ©∫ÊâÄÔºâ</label>
            <Textarea value={text} onChange={(e) => setText(e.target.value)} />
          </div>
          <div>
            <label className="block text-sm mb-1">ÂõûÁ≠îÔºà‰æã: 1:answer ÊîπË°åÂå∫Âàá„ÇäÔºâ</label>
            <Textarea value={answers} onChange={(e) => setAnswers(e.target.value)} />
          </div>
          <label className="inline-flex items-center gap-2">
            <input type="checkbox" checked={caseSensitive} onChange={(e) => setCaseSensitive(e.target.checked)} />
            <span className="text-sm">Â§ßÊñáÂ≠óÂ∞èÊñáÂ≠ó„ÇíÂå∫Âà•</span>
          </label>
        </div>
      )}
      <div className="flex justify-end">
        <Button type="submit">ËøΩÂä†</Button>
      </div>
    </form>
  );
}

"use client";
import * as React from "react";
import { useVirtualizer } from "@tanstack/react-virtual";
import { listLessons, listCards, listFlaggedByCourse, getProgress, listCourses, useLocalDbVersion } from "@/lib/localdb";
import type { UUID, Card, Lesson, CardType, Course } from "@/lib/types";
import { Input } from "@/components/ui/input";
import { Select } from "@/components/ui/select";
import { ProgressRing } from "@/components/ui/progress-ring";
import { Badge } from "@/components/ui/badge";
import {
  DropdownMenu,
  DropdownMenuTrigger,
  DropdownMenuContent,
  DropdownMenuItem,
} from "@/components/ui/dropdown-menu";

type Props = {
  courseId: UUID;
  selectedId?: UUID;
  onSelect: (id: UUID, kind: "course" | "lesson" | "card" | "lesson-edit") => void;
};

export function NavTree({ courseId, selectedId, onSelect }: Props) {
  // DBÂ§âÊõ¥„Å´ËøΩÂæì
  const dbv = useLocalDbVersion();
  const [q, setQ] = React.useState("");
  const [courses, setCourses] = React.useState<Course[]>([]);
  const [lessons, setLessons] = React.useState<Lesson[]>([]);
  const [expanded, setExpanded] = React.useState<Record<string, boolean>>({});
  const containerRef = React.useRef<HTMLDivElement>(null);
  const [typeFilter, setTypeFilter] = React.useState<"all" | CardType>("all");
  const [onlyFlagged, setOnlyFlagged] = React.useState(false);
  const [onlyUnlearned, setOnlyUnlearned] = React.useState(false);
  const flaggedSet = React.useMemo(() => new Set(listFlaggedByCourse(courseId)), [courseId, dbv]);

  // „É≠„Éº„Éì„É≥„Ç∞ tabindex Áî®„ÅÆ"„Ç¢„ÇØ„ÉÜ„Ç£„Éñ"È†ÖÁõÆÁÆ°ÁêÜ
  const [activeId, setActiveId] = React.useState<string | undefined>(undefined);

  React.useEffect(() => {
    setCourses(listCourses());
    setLessons(listLessons(courseId));
    // ÂàùÊúüË°®Á§∫„Åß„ÅØÁèæÂú®„ÅÆ„Ç≥„Éº„Çπ„ÇíÂ±ïÈñã
    setExpanded((m) => (m[`co:${courseId}`] ? m : { ...m, [`co:${courseId}`]: true }));
  }, [courseId, dbv]);

  // ÂèØË¶ñË°å„Çí„Éï„É©„ÉÉ„ÉàÂåñ
  type Row = { key: string; type: "course" | "lesson" | "card"; id: string; level: number; title: string; tags?: string[]; completed?: boolean; expanded?: boolean };
  const rows: Row[] = React.useMemo(() => {
    const out: Row[] = [];
    const match = (s: string) => (q ? s.toLowerCase().includes(q.toLowerCase()) : true);
    // „Ç≥„Éº„Çπ ‚Üí „É¨„ÉÉ„Çπ„É≥ ‚Üí „Ç´„Éº„Éâ
    courses.forEach((co) => {
      // Â≠ê„Å©„ÇÇ„Å´Ê§úÁ¥¢/„Éï„Ç£„É´„Çø„Åå„Åã„Åã„Å£„Å¶„ÅÑ„Çå„Å∞Ë¶™„ÅØË°®Á§∫
      const ls = listLessons(co.id);
      const hasChild = ls.some((l) => {
        const cs = listCards(l.id);
        return (
          match(l.title) ||
          cs.some((c) => {
            if (typeFilter !== "all" && c.cardType !== typeFilter) return false;
            if (onlyFlagged && !flaggedSet.has(c.id)) return false;
            if (onlyUnlearned && getProgress(c.id)?.completed) return false;
            return match(c.title ?? "") || match(c.cardType);
          })
        );
      });
      if (!(match(co.title) || hasChild)) return;

      out.push({ key: `co:${co.id}`, type: "course", id: co.id, level: 1, title: co.title, expanded: !!expanded[`co:${co.id}`] });
      if (expanded[`co:${co.id}`]) {
        ls.forEach((l) => {
          const childAny = (() => {
            const cs = listCards(l.id);
            return cs.some((c) => {
              if (typeFilter !== "all" && c.cardType !== typeFilter) return false;
              if (onlyFlagged && !flaggedSet.has(c.id)) return false;
              if (onlyUnlearned && getProgress(c.id)?.completed) return false;
              return match(c.title ?? "") || match(c.cardType);
            });
          })();
          if (!(match(l.title) || childAny)) return;
          out.push({ key: `l:${l.id}`, type: "lesson", id: l.id, level: 2, title: l.title, expanded: !!expanded[`le:${l.id}`] });
          if (expanded[`le:${l.id}`]) {
            const cs = listCards(l.id);
            cs.forEach((c) => {
              if (typeFilter !== "all" && c.cardType !== typeFilter) return;
              if (onlyFlagged && !flaggedSet.has(c.id)) return;
              if (onlyUnlearned && getProgress(c.id)?.completed) return;
              if (match(c.title ?? "") || match(c.cardType))
                out.push({ key: `c:${c.id}`, type: "card", id: c.id, level: 3, title: labelForCard(c), tags: c.tags ?? [], completed: !!getProgress(c.id)?.completed });
            });
          }
        });
      }
    });
    return out;
  }, [courses, expanded, q, typeFilter, onlyFlagged, onlyUnlearned, flaggedSet, dbv]);

  // „É¨„ÉÉ„Çπ„É≥Âçò‰Ωç„ÅÆÈÄ≤ÊçóÁéá
  const lessonProgress = React.useMemo(() => {
    const map = new Map<string, number>();
    for (const l of lessons) {
      const cs = listCards(l.id);
      if (cs.length === 0) { map.set(l.id, 0); continue; }
      const done = cs.reduce((acc, c) => acc + (getProgress(c.id)?.completed ? 1 : 0), 0);
      map.set(l.id, Math.round((done / cs.length) * 100));
    }
    return map;
  }, [lessons, dbv]);

  // „Éê„Éº„ÉÅ„É£„É´„É™„Çπ„ÉàÔºà@tanstack/react-virtualÔºâ
  const COURSE_H = 40; // px
  const LESSON_H = 36; // px
  const CARD_H = 28; // px
  const rowVirtualizer = useVirtualizer({
    count: rows.length,
    getScrollElement: () => containerRef.current,
    estimateSize: (index) => (rows[index]?.type === "course" ? COURSE_H : rows[index]?.type === "lesson" ? LESSON_H : CARD_H),
    overscan: 6,
  });

  // ÂàùÂõû„Éï„Ç©„Éº„Ç´„ÇπÊôÇ„Å´ÂÖàÈ†≠„Å∏„É≠„Éº„Éì„É≥„Ç∞
  const onTreeFocus = (e: React.FocusEvent) => {
    if (!containerRef.current) return;
    const hasFocusedItem = !!containerRef.current.querySelector('[role="treeitem"][tabindex="0"]');
    if (!hasFocusedItem && rows.length) setActiveId(rows[0].id);
  };

  // APG Ê∫ñÊã†„ÅÆ„Ç≠„Éº„Éú„Éº„Éâ„É¢„Éá„É´
  const onKeyDown = (e: React.KeyboardEvent) => {
    if (!rows.length) return;
    const idx = rows.findIndex((r) => r.id === activeId);
    const cur = rows[Math.max(0, idx)];
    switch (e.key) {
      case "ArrowDown": {
        e.preventDefault();
        const nextIndex = Math.min(rows.length - 1, Math.max(0, idx + 1));
        const next = rows[nextIndex];
        setActiveId(next.id);
        rowVirtualizer.scrollToIndex(nextIndex, { align: "auto" });
        break;
      }
      case "ArrowUp": {
        e.preventDefault();
        const prevIndex = Math.max(0, idx - 1);
        const prev = rows[prevIndex];
        setActiveId(prev.id);
        rowVirtualizer.scrollToIndex(prevIndex, { align: "auto" });
        break;
      }
      case "Home": {
        e.preventDefault();
        setActiveId(rows[0].id);
        rowVirtualizer.scrollToIndex(0, { align: "start" });
        break;
      }
      case "End": {
        e.preventDefault();
        const last = rows.length - 1;
        setActiveId(rows[last].id);
        rowVirtualizer.scrollToIndex(last, { align: "end" });
        break;
      }
      case "ArrowRight": {
        if (cur?.type === "course") {
          const k = `co:${cur.id}`;
          const isOpen = !!expanded[k];
          if (!isOpen) { e.preventDefault(); setExpanded((m) => ({ ...m, [k]: true })); }
        } else if (cur?.type === "lesson") {
          const k = `le:${cur.id}`;
          const isOpen = !!expanded[k];
          if (!isOpen) {
            e.preventDefault();
            setExpanded((m) => ({ ...m, [k]: true }));
          } else {
            // ÊúÄÂàù„ÅÆÂ≠ê„Å∏
            const next = rows[idx + 1];
            if (next?.type === "card") {
              e.preventDefault();
              setActiveId(next.id);
              rowVirtualizer.scrollToIndex(idx + 1, { align: "auto" });
            }
          }
        }
        break;
      }
      case "ArrowLeft": {
        if (cur?.type === "course") {
          const k = `co:${cur.id}`;
          const isOpen = !!expanded[k];
          if (isOpen) { e.preventDefault(); setExpanded((m) => ({ ...m, [k]: false })); }
        } else if (cur?.type === "lesson") {
          const k = `le:${cur.id}`;
          const isOpen = !!expanded[k];
          if (isOpen) { e.preventDefault(); setExpanded((m) => ({ ...m, [k]: false })); }
        } else if (cur?.type === "card") {
          // Ë¶™„ÅÆ„É¨„ÉÉ„Çπ„É≥„Å∏ÁßªÂãï
          for (let i = idx - 1; i >= 0; i--) {
            if (rows[i].type === "lesson") { e.preventDefault(); setActiveId(rows[i].id); rowVirtualizer.scrollToIndex(i, { align: "auto" }); break; }
          }
        }
        break;
      }
      case "Enter": {
        e.preventDefault();
        if (cur) onSelect(cur.id as UUID, cur.type);
        break;
      }
      case " ": {
        if (cur?.type === "course") {
          e.preventDefault();
          setExpanded((m) => ({ ...m, [`co:${cur.id}`]: !m[`co:${cur.id}`] }));
        } else if (cur?.type === "lesson") {
          e.preventDefault();
          setExpanded((m) => ({ ...m, [`le:${cur.id}`]: !m[`le:${cur.id}`] }));
        }
        break;
      }
    }
  };

  return (
    <aside className="h-full flex flex-col">
      <div className="p-2 border-b grid grid-cols-1 gap-2">
        <div>
          <label className="sr-only" htmlFor="tree-search">Ê§úÁ¥¢</label>
          <Input id="tree-search" value={q} onChange={(e) => setQ(e.target.value)} placeholder="Ê§úÁ¥¢Ôºà„Çø„Ç§„Éà„É´/„Çø„Ç§„ÉóÔºâ" />
        </div>
        <div className="flex items-center gap-2 text-xs">
          <label className="text-gray-600" htmlFor="type-filter">Á®ÆÈ°û</label>
          <Select id="type-filter" value={typeFilter} onChange={(e) => setTypeFilter(e.target.value as any)} className="max-w-[140px]">
            <option value="all">„Åô„Åπ„Å¶</option>
            <option value="text">Text</option>
            <option value="quiz">Quiz</option>
            <option value="fill-blank">Fill‚Äëblank</option>
          </Select>
          <label className="inline-flex items-center gap-1 ml-2">
            <input type="checkbox" checked={onlyFlagged} onChange={(e) => setOnlyFlagged(e.target.checked)} />
            „Éï„É©„Ç∞„ÅÆ„Åø
          </label>
          <label className="inline-flex items-center gap-1 ml-2">
            <input type="checkbox" checked={onlyUnlearned} onChange={(e) => setOnlyUnlearned(e.target.checked)} />
            Êú™Â≠¶Áøí„ÅÆ„Åø
          </label>
        </div>
      </div>
      <div
        ref={containerRef}
        role="tree"
        aria-label="„Ç≥„Éº„ÇπÊßãÈÄ†"
        className="flex-1 overflow-auto py-2"
        onKeyDown={onKeyDown}
        onFocus={onTreeFocus}
        tabIndex={0}
      >
        <div style={{ height: rowVirtualizer.getTotalSize(), position: "relative" }}>
          {rowVirtualizer.getVirtualItems().map((vi) => {
            const r = rows[vi.index];
            return (
              <div
                key={r.key}
                style={{ position: "absolute", top: 0, left: 0, right: 0, transform: `translateY(${vi.start}px)` }}
              >
                {r.type === "course" ? (
                  <TreeCourseRow
                    id={r.id}
                    title={r.title}
                    level={1}
                    expanded={!!expanded[`co:${r.id}`]}
                    selected={r.id === courseId}
                    active={activeId === r.id}
                    onClick={() => onSelect(r.id as UUID, "course")}
                    onToggle={() => setExpanded((m) => ({ ...m, [`co:${r.id}`]: !m[`co:${r.id}`] }))}
                    onActive={() => setActiveId(r.id)}
                  />
                ) : r.type === "lesson" ? (
                  <TreeLessonRow
                    id={r.id}
                    title={r.title}
                    level={2}
                    expanded={!!expanded[`le:${r.id}`]}
                    selected={selectedId === r.id}
                    active={activeId === r.id}
                    progressPct={lessonProgress.get(r.id) ?? 0}
                    onClick={() => onSelect(r.id as UUID, "lesson")}
                    onToggle={() => setExpanded((m) => ({ ...m, [`le:${r.id}`]: !m[`le:${r.id}`] }))}
                    onActive={() => setActiveId(r.id)}
                    onEdit={() => onSelect(r.id as UUID, "lesson-edit")}
                  />
                ) : (
                  <TreeCardRow
                    id={r.id}
                    title={r.title}
                    level={3}
                    selected={selectedId === r.id}
                    active={activeId === r.id}
                    tags={r.tags ?? []}
                    completed={!!r.completed}
                    onClick={() => onSelect(r.id as UUID, "card")}
                    onActive={() => setActiveId(r.id)}
                  />
                )}
              </div>
            );
          })}
        </div>
      </div>
    </aside>
  );
}

