"use client";
import type { UUID, CardType } from "@/lib/types";
import { draftsPut, draftsGet, draftsDelete, type DraftRow } from "@/lib/idb";
import { updateCard } from "@/lib/localdb";

// Inspector Áî®„ÅÆ‰∏ãÊõ∏„Åç‰øùÂ≠ò + ÂÖ¨Èñã

export type SaveCardDraftInput =
  | { cardId: UUID; cardType: "text"; title?: string | null; tags?: string[]; body: string }
  | { cardId: UUID; cardType: "quiz"; title?: string | null; tags?: string[]; question: string; options: string[]; answerIndex: number; explanation?: string | null }
  | { cardId: UUID; cardType: "fill-blank"; title?: string | null; tags?: string[]; text: string; answers: Record<string, string>; caseSensitive?: boolean };

export async function saveCardDraft(input: SaveCardDraftInput): Promise<{ updatedAt: string }> {
  const key = `card:${input.cardId}`;
  const updatedAt = new Date().toISOString();
  const row: DraftRow = {
    key,
    cardId: input.cardId,
    cardType: input.cardType,
    title: (input as any).title ?? null,
    data: input,
    updatedAt,
  };
  await draftsPut(row);
  return { updatedAt };
}

export async function loadCardDraft(cardId: UUID): Promise<SaveCardDraftInput | undefined> {
  const row = await draftsGet(`card:${cardId}`);
  return row?.data as SaveCardDraftInput | undefined;
}

export async function publishCard(cardId: UUID): Promise<void> {
  const row = await draftsGet(`card:${cardId}`);
  if (!row) return;
  const d = row.data as SaveCardDraftInput;
  if (d.cardType === "text") {
    await updateCard(d.cardId, {
      title: d.title ?? null,
      tags: d.tags ?? undefined,
      content: { body: d.body },
    });
  } else if (d.cardType === "quiz") {
    await updateCard(d.cardId, {
      title: d.title ?? null,
      tags: d.tags ?? undefined,
      content: {
        question: d.question,
        options: d.options,
        answerIndex: d.answerIndex,
        explanation: d.explanation ?? undefined,
      },
    });
  } else if (d.cardType === "fill-blank") {
    await updateCard(d.cardId, {
      title: d.title ?? null,
      tags: d.tags ?? undefined,
      content: {
        text: d.text,
        answers: d.answers,
        caseSensitive: d.caseSensitive ?? false,
      },
    });
  }
  await draftsDelete(`card:${cardId}`);
}

import { createClient, getCurrentUserId } from "@/lib/supabase/server";
import type { Course, Lesson, Card, Progress, UUID, SrsEntry } from "@/lib/types";

// All functions here run on the server and use the authenticated
// Supabase session (RLS enforced).

const mapCourse = (r: any): Course => ({
  id: r.id,
  title: r.title,
  description: r.description ?? undefined,
  category: r.category ?? undefined,
  status: r.status,
  createdAt: r.created_at,
  updatedAt: r.updated_at,
});

const mapLesson = (r: any): Lesson => ({
  id: r.id,
  courseId: r.course_id,
  title: r.title,
  orderIndex: r.order_index,
  createdAt: r.created_at,
});

const mapCard = (r: any): Card => ({
  id: r.id,
  lessonId: r.lesson_id,
  cardType: r.card_type,
  title: r.title ?? null,
  tags: r.tags ?? undefined,
  content: r.content,
  orderIndex: r.order_index,
  createdAt: r.created_at,
});

export async function listCourses(): Promise<Course[]> {
  const supa = await createClient();
  const { data, error } = await supa
    .from("courses")
    .select("*")
    .order("updated_at", { ascending: false });
  if (error) throw error;
  return (data ?? []).map(mapCourse);
}

export async function getCourse(courseId: UUID): Promise<Course | undefined> {
  const supa = await createClient();
  const { data, error } = await supa
    .from("courses")
    .select("*")
    .eq("id", courseId)
    .maybeSingle();
  if (error) throw error;
  return data ? mapCourse(data) : undefined;
}

export async function listLessons(courseId: UUID): Promise<Lesson[]> {
  const supa = await createClient();
  const { data, error } = await supa
    .from("lessons")
    .select("*")
    .eq("course_id", courseId)
    .order("order_index")
    .order("created_at");
  if (error) throw error;
  return (data ?? []).map(mapLesson);
}

export async function listCards(lessonId: UUID): Promise<Card[]> {
  const supa = await createClient();
  const { data, error } = await supa
    .from("cards")
    .select("*")
    .eq("lesson_id", lessonId)
    .order("order_index")
    .order("created_at");
  if (error) throw error;
  return (data ?? []).map(mapCard);
}

export async function listFlaggedByCourse(courseId: UUID): Promise<UUID[]> {
  const supa = await createClient();
  const { data, error } = await supa
    .from("flags")
    .select("card_id, cards!inner(lesson_id, lessons!inner(course_id))")
    .eq("cards.lessons.course_id", courseId);
  if (error) throw error;
  return (data ?? []).map((r: any) => r.card_id);
}

export async function getProgress(cardId: UUID): Promise<Progress | undefined> {
  const supa = await createClient();
  const { data, error } = await supa
    .from("progress")
    .select("*")
    .eq("card_id", cardId)
    .maybeSingle();
  if (error) throw error;
  return data
    ? {
        cardId: data.card_id,
        completed: data.completed,
        completedAt: data.completed_at ?? undefined,
        answer: data.answer ?? undefined,
      }
    : undefined;
}

export async function getNote(cardId: UUID): Promise<string | undefined> {
  const supa = await createClient();
  const { data, error } = await supa
    .from("notes")
    .select("text")
    .eq("card_id", cardId)
    .maybeSingle();
  if (error) throw error;
  return data?.text ?? undefined;
}

export async function snapshot() {
  const supa = await createClient();

  const [coursesRes, lessonsRes, cardsRes, progressRes, flagsRes, notesRes] = await Promise.all([
    supa.from("courses").select("*"),
    supa.from("lessons").select("*"),
    supa.from("cards").select("*"),
    supa.from("progress").select("*"),
    supa.from("flags").select("*"),
    supa.from("notes").select("*"),
  ]);

  const throwIf = (r: any) => { if (r.error) throw r.error; return r.data ?? []; };
  const courses = throwIf(coursesRes).map(mapCourse) as Course[];
  const lessons = throwIf(lessonsRes).map(mapLesson) as Lesson[];
  const cards = throwIf(cardsRes).map(mapCard) as Card[];
  const progress = throwIf(progressRes).map((p: any) => ({
    cardId: p.card_id,
    completed: p.completed,
    completedAt: p.completed_at ?? undefined,
    answer: p.answer ?? undefined,
  })) as Progress[];
  const flags = throwIf(flagsRes).map((f: any) => ({ cardId: f.card_id, flaggedAt: f.flagged_at })) as { cardId: UUID; flaggedAt: string }[];
  const notes = throwIf(notesRes).map((n: any) => ({ cardId: n.card_id, text: n.text, updatedAt: n.updated_at })) as { cardId: UUID; text: string; updatedAt: string }[];

  return { courses, lessons, cards, progress, flags, notes };
}

export async function upsertSrs(entry: SrsEntry): Promise<SrsEntry> {
  const supa = await createClient();
  const userId = await getCurrentUserId();
  if (!userId) throw new Error("Not authenticated");
  const { data, error } = await supa
    .from("srs")
    .upsert({
      user_id: userId,
      card_id: entry.cardId,
      ease: entry.ease,
      interval: entry.interval,
      due: entry.due.slice(0, 10),
      last_rating: entry.lastRating ?? null,
    })
    .select("*")
    .maybeSingle();
  if (error) throw error;
  return {
    cardId: data!.card_id,
    ease: data!.ease,
    interval: data!.interval,
    due: new Date(data!.due).toISOString(),
    lastRating: data!.last_rating ?? undefined,
  } satisfies SrsEntry;
}

"use client";
import * as React from "react";

import type {
  AiDraft,
  Card,
  Course,
  Lesson,
  LessonCards,
  Progress,
  UUID,
  CoursePlan,
  SrsEntry,
  SrsRating,
} from "./types";

// --- Client cache + pub/sub (no localStorage persistence) ------------------
let __dbVersion = 0;
const __listeners = new Set<() => void>();
let __bc: BroadcastChannel | null = null;

type DB = {
  courses: Course[];
  lessons: Lesson[];
  cards: Card[];
  progress: Progress[];
  srs: SrsEntry[];
  flags: { cardId: UUID; flaggedAt: string }[];
  notes: { cardId: UUID; text: string; updatedAt: string }[];
};

function emptyDb(): DB {
  return { courses: [], lessons: [], cards: [], progress: [], srs: [], flags: [], notes: [] };
}

let __db: DB = emptyDb();

function __notifyDbChange() {
  __dbVersion++;
  __listeners.forEach((l) => { try { l(); } catch {} });
  try { __bc?.postMessage({ type: "db-change", v: __dbVersion }); } catch {}
}

if (typeof window !== "undefined") {
  try { __bc = new BroadcastChannel("learnify_sync"); } catch {}
  __bc && (__bc.onmessage = (ev) => {
    if (ev?.data?.type === "db-change") {
      __dbVersion = Math.max(__dbVersion, Number(ev.data.v) || 0);
      __listeners.forEach((l) => { try { l(); } catch {} });
    }
  });
  // Initial sync
  void refreshAll();
}

export function subscribeLocalDb(listener: () => void): () => void {
  __listeners.add(listener);
  return () => { __listeners.delete(listener); };
}

export function useLocalDbVersion(): number {
  return React.useSyncExternalStore(subscribeLocalDb, () => __dbVersion, () => __dbVersion);
}

async function api<T = unknown>(op: string, params?: any): Promise<T> {
  const res = await fetch("/api/db", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ op, params }),
  });
  if (!res.ok) throw new Error(await res.text());
  return (await res.json()) as T;
}

export async function refreshAll() {
  const snap = await api<DB & { notes: { cardId: UUID; text: string; updatedAt: string } }>("snapshot");
  __db = { ...__db, ...snap };
  __notifyDbChange();
}

// ----- Reads (sync from cache) --------------------------------------------
export function listCourses(): Course[] {
  return [...__db.courses].sort((a, b) => b.updatedAt.localeCompare(a.updatedAt));
}

export function getCourse(courseId: UUID): Course | undefined {
  return __db.courses.find((c) => c.id === courseId);
}

export function listLessons(courseId: UUID): Lesson[] {
  return __db.lessons
    .filter((l) => l.courseId === courseId)
    .sort((a, b) => a.orderIndex - b.orderIndex || a.createdAt.localeCompare(b.createdAt));
}

export function listCards(lessonId: UUID): Card[] {
  return __db.cards
    .filter((c) => c.lessonId === lessonId)
    .sort((a, b) => a.orderIndex - b.orderIndex || a.createdAt.localeCompare(b.createdAt));
}

export function getProgress(cardId: UUID): Progress | undefined {
  return __db.progress.find((p) => p.cardId === cardId);
}

export function isFlagged(cardId: UUID): boolean {
  return __db.flags.some((f) => f.cardId === cardId);
}

export function getNote(cardId: UUID): string | undefined {
  return __db.notes.find((n) => n.cardId === cardId)?.text;
}

export function listFlaggedByCourse(courseId: UUID): UUID[] {
  const lessonIds = __db.lessons.filter((l) => l.courseId === courseId).map((l) => l.id);
  const cardIds = __db.cards.filter((c) => lessonIds.includes(c.lessonId)).map((c) => c.id);
  const set = new Set(cardIds);
  return __db.flags.filter((f) => set.has(f.cardId)).map((f) => f.cardId);
}

// ----- Writes (async, then refresh cache) ---------------------------------
export async function createCourse(input: { title: string; description?: string; category?: string }): Promise<{ courseId: UUID }> {
  const res = await api<{ courseId: UUID }>("createCourse", input);
  await refreshAll();
  return res;
}

export async function updateCourse(courseId: UUID, patch: Partial<Course>): Promise<void> {
  await api("updateCourse", { courseId, patch });
  await refreshAll();
}

export async function deleteCourse(courseId: UUID): Promise<void> {
  await api("deleteCourse", { courseId });
  await refreshAll();
}

export async function addLesson(courseId: UUID, title: string): Promise<{ lessonId: UUID }> {
  const res = await api<{ lessonId: UUID }>("addLesson", { courseId, title });
  await refreshAll();
  return res;
}

export async function deleteLesson(lessonId: UUID): Promise<void> {
  await api("deleteLesson", { lessonId });
  await refreshAll();
}

export async function reorderLessons(courseId: UUID, orderedIds: UUID[]): Promise<void> {
  await api("reorderLessons", { courseId, orderedIds });
  await refreshAll();
}

export async function addCard(
  lessonId: UUID,
  card: Omit<Card, "id" | "lessonId" | "createdAt" | "orderIndex">
): Promise<UUID> {
  const { id } = await api<{ id: UUID }>("addCard", { lessonId, card });
  await refreshAll();
  return id;
}

export async function updateCard(cardId: UUID, patch: Partial<Card>): Promise<void> {
  await api("updateCard", { cardId, patch });
  await refreshAll();
}

export async function deleteCard(cardId: UUID): Promise<void> {
  await api("deleteCard", { cardId });
  await refreshAll();
}

export async function deleteCards(ids: UUID[]): Promise<void> {
  await api("deleteCards", { ids });
  await refreshAll();
}

export async function reorderCards(lessonId: UUID, orderedIds: UUID[]): Promise<void> {
  await api("reorderCards", { lessonId, orderedIds });
  await refreshAll();
}

export async function saveProgress(input: Progress): Promise<void> {
  await api("saveProgress", { input });
  const idx = __db.progress.findIndex((p) => p.cardId === input.cardId);
  if (idx === -1) __db.progress.push(input); else __db.progress[idx] = input;
  __notifyDbChange();
}

export async function rateSrs(cardId: UUID, rating: SrsRating): Promise<SrsEntry> {
  const entry = await api<SrsEntry>("rateSrs", { cardId, rating });
  const idx = __db.srs.findIndex((s) => s.cardId === cardId);
  if (idx === -1) __db.srs.push(entry); else __db.srs[idx] = entry;
  __notifyDbChange();
  return entry;
}

export async function toggleFlag(cardId: UUID): Promise<boolean> {
  const { on } = await api<{ on: boolean }>("toggleFlag", { cardId });
  const idx = __db.flags.findIndex((f) => f.cardId === cardId);
  if (on && idx === -1) __db.flags.push({ cardId, flaggedAt: new Date().toISOString() });
  if (!on && idx !== -1) __db.flags.splice(idx, 1);
  __notifyDbChange();
  return on;
}

export async function saveNote(cardId: UUID, text: string): Promise<void> {
  await api("saveNote", { cardId, text });
  const idx = __db.notes.findIndex((n) => n.cardId === cardId);
  const row = { cardId, text, updatedAt: new Date().toISOString() };
  if (idx === -1) __db.notes.push(row); else __db.notes[idx] = row;
  __notifyDbChange();
}

